local AccountUtil = require("Utils/AccountUtil")
local LoginNewDefine = require("Game/LoginNew/LoginNewDefine")
local VoiceDefine = require("Game/Voice/VoiceDefine")

---@class LoginUtils
local LoginUtils = {}

function LoginUtils:OpenAgreementUrl(LinkID)
    if LinkID == LoginNewDefine.AgreementEnum.Contract then
        AccountUtil.OpenUrl(LoginNewDefine.AgreementUrls.Contract, 1, false, true, "", false)
    elseif LinkID == LoginNewDefine.AgreementEnum.Privacy then
        AccountUtil.OpenUrl(LoginNewDefine.AgreementUrls.Privacy, 1, false, true, "", false)
    elseif LinkID == LoginNewDefine.AgreementEnum.ChildrenPrivacy then
        AccountUtil.OpenUrl(LoginNewDefine.AgreementUrls.ChildrenPrivacy, 1, false, true, "", false)
    elseif LinkID == LoginNewDefine.AgreementEnum.PrivacySDK then
        AccountUtil.OpenUrl(LoginNewDefine.AgreementUrls.PrivacySDK, 1, false, true, "", false)
    elseif LinkID == LoginNewDefine.AgreementEnum.SelfDisciplinePact then
        AccountUtil.OpenUrl(LoginNewDefine.AgreementUrls.SelfDisciplinePact, 1, false, true, "", false)
    end
end

--- 获取当前环境的GVoice服务器Url
function LoginUtils:GetGVoiceServerUrl()
    if VoiceDefine.IsTestDev then
        return LoginNewDefine.GVoiceServerTestUrl
    end

    local AreaID = _G.LoginMgr.OverseasSvrAreaId or LoginNewDefine.OverseasSvrAreaId.None
    return LoginNewDefine.GVoiceServerUrl[AreaID]
end

return LoginUtils